import React, { Fragment, useState, useEffect } from 'react';
import EmployeeList from './components/EmployeeList';
import Search from './components/Search';
import Nav from './components/Nav';
import EmployeeContext from './utils/EmployeeContext';
import API from './utils/API';
import './App.css';

const App = () => {
  const [employees, setEmployees] = useState({
    employees: [],
    displayedEmployees: [],
    sortOrder: 'desending'
  });

  const handleSearch = e => {
    const searchTerm = e.target.value;
    console.log(searchTerm);
    console.log(employees.employees);
    const searchList = employees.employees.filter(employee => {
      let nameVal = `${employee.name.first.toLowerCase()} ${employee.name.last.toLowerCase()}`;
      console.log(nameVal);
      if (nameVal.indexOf(searchTerm.toLowerCase()) !== -1) {
        return employee;
      }
    });
    setEmployees({ ...employees, employees: searchList });
  };

  useEffect(() => {
    API.getUsers().then(results => {
      setEmployees({
        ...employees,
        employees: results.data.results,
        displayedUsers: results.data.results
      });
    });
  }, []);
  return (
    <Fragment>
      <Nav />
      <EmployeeContext.Provider value={{ employees, handleSearch }}>
        <Search />
        <div className='main-container'>
          <EmployeeList />
        </div>
      </EmployeeContext.Provider>
    </Fragment>
  );
};

export default App;
